INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (2, 'Un luogo incantevole con acque cristalline e servizi di prima classe. Ideale per trascorrere una giornata rilassante sotto il sole salentino.', 'Gallipoli', 'Lido Punta della Suina', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 4);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (3, 'Situato in una baia mozzafiato, questo lido offre paesaggi mozzafiato e una vasta gamma di attività acquatiche. Perfetto per famiglie e amanti del mare.', 'Otranto', 'Lido Baia dei Turchi', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 5);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (4, 'Un''oasi di tranquillità con acque termali naturali. Le piscine naturali rendono questo lido unico nel suo genere. Un luogo perfetto per rilassarsi e rigenerarsi.', 'Santa Cesarea Terme', 'Lido La Poesia', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 3);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (5, 'Un mix di divertimento e relax, con una spiaggia dorata e servizi moderni. Ideale per chi cerca una giornata di sole e mare senza rinunciare al comfort.', 'Porto Cesareo', 'Lido Tabù', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 4);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (6, 'Una delle spiagge più famose della regione, con le sue imponenti scogliere e acque cristalline. Perfetto per gli amanti della natura e della tranquillità.', 'Torre dell''Orso', 'Lido Le Orsette', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 5);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (7, 'Un lido ideale per le famiglie, con sabbia fine e acque poco profonde. Numerose attività per bambini e adulti rendono questo lido una scelta versatile per tutti.', 'Torre Lapillo', 'Lido degli Angeli', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 4);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (8, 'Situato all''estremità meridionale del Salento, questo lido offre una vista mozzafiato e un''atmosfera unica. Perfetto per chi cerca un''esperienza autentica.', 'Leuca', 'Lido Marina di Leuca', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 3);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (9, 'Un lido pittoresco con una lunga spiaggia dorata e acque cristalline. Ideale per chi ama lunghe passeggiate sulla spiaggia e tramonti indimenticabili.', 'Torre Pali', 'Lido Pali', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 5);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (10, 'Situato in una delle zone più incontaminate del Salento, questo lido offre chilometri di spiagge di sabbia bianca. Perfetto per chi cerca tranquillità e bellezza naturale.', 'Punta Prosciutto', 'Lido degli Achei', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 3);
INSERT INTO public.resorts (id, description, location, name, photo_cover, rating) VALUES (11, 'Una destinazione amata da chi cerca una spiaggia meno affollata. Spiagge incontaminate e un''atmosfera rilassata fanno di questo lido una scelta perfetta per la fuga dal caos.', 'Torre Vado', 'Lido Venere', 'https://storage.googleapis.com/pod_public/1300/122734.jpg', 4);
